    <div class=\"container\">
        <h1>{{ product.name }}</h1>
        <div>ID : {{ product.id }} </div>
        <div>Description : {{ product.description }} </div>
        <div>Prix : {{ product.priceHT }}</div>
        <div>Disponbilité : {{ product.available }}</div>

